import React from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles, AppBar, Grid, MenuItem, IconButton, Popover, Box, Snackbar, Container, Link } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import { ExpandMore } from '@material-ui/icons';
import PopupState, { bindPopover, bindTrigger } from 'material-ui-popup-state';
import stylePage from './style';
import { bandES, bandEnglish, bandDE, bandET, bandFR, bandIT, bandPT, bandRU, bannerLogo } from '../icons';

export const APP_SETTINGS = JSON.parse(localStorage.getItem('APP_SETTINGS')) !== null ? JSON.parse(localStorage.getItem('APP_SETTINGS')) : [{}];

class Header extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      countdown: {
        days: '',
        hours: '',
        mins: '',
        secs: '',
      },
      openAlert: false,
      openAlertType: '',
      openAlertText: '',
      drawer: false,
      expanded: 'panel1',
      amount: '500',
      currency: 'USDT',
      currency_symbol: 'USDT',
      guarantee_amount: '',
      guarantee_currency: 'BTC',
      contract_type: 1,
      installmentSelected: '',
      listInstallments: [],
