import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles, Grid, Typography, Container, Input, Link, Modal } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import styles from './style';
import PrimaryButton from '../../components/customButtom';
import Header from '../../components/header';

class Deposito extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isModalOpen: false,
      inputValue: '',
    };
  }

  handleOpenModal = () => {
    this.setState({ isModalOpen: true });
  };

  handleCloseModal = () => {
    this.setState({ isModalOpen: false });
  };

  handleInputChange = (e) => {
    this.setState({ inputValue: e.target.value });
  };

  handleConfirmValue = () => {
    this.handleCloseModal();
  };
  render() {
    const { classes } = this.props;
    const { isModalOpen, inputValue } = this.state;

    return (
      <>
        <Header />
        <Container>
          <Grid className={classes.container}>
            <div className={classes.cardTitle}>
              <Typography component='h1'>Insira o valor para Deposito</Typography>
            </div>
            <div className={classes.centraliza}>
              <div className={classes.cardInputs}>
                <div>
                  <Input placeholder='Informe o valor' type='Number' disableUnderline={true} className={classes.input} value={inputValue} onChange={this.handleInputChange} />
                </div>
              </div>
              <div className={classes.cardInputs}>
                <div>
                  <Input placeholder='Numero do envelope' type='Number' maxLength={12} disableUnderline={true} className={classes.input} value={inputValue} />
                </div>
              </div>
            </div>
            <div className={classes.cardTitle}>
              <PrimaryButton text='Confirmar' onClick={this.handleOpenModal} />
              <Modal
                open={isModalOpen}
                onClose={this.handleCloseModal}
                style={{
                  display: 'grid',
                  alignItems: 'center',
                  justifyContent: 'center',
                  background: 'transparent',
                }}
              >
                <div className={classes.cardTitle}>
                  <Typography component='h1'>Confirmar valor</Typography>
                </div>
                <div className={classes.cardInputs}>
                  <div>
                    <Input placeholder='Informe o valor' type='Number' disableUnderline={true} className={classes.input} />
                  </div>
                  <Link href='/'>
                    <PrimaryButton text='Confirmar' />
                  </Link>
                </div>
              </Modal>
            </div>
          </Grid>
        </Container>
      </>
    );
  }
}

export default withRouter(withStyles(styles)(Deposito));
