import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles, Grid, Typography, Container, Breadcrumbs, Link } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import styles from './style';
import axios from 'axios';
import { bannerMoeda } from '../../components/icons';
import Header from '../../components/header';
import Footer from '../../components/footer';
import ExchangeInput from '../../components/Input';

class Cambio extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue1: '',
      inputValue2: '',
      selecte1: 'EUR',
      selecte2: 'BRL',
      calculaValue: 0.0,
      calculaCosts: 0.0,
      valueTaxa: 0.0,
      exchangeRateEuroToBRL: 1.0,
      exchangeRateBRLToEuro: 1.0,
    };
  }
  componentDidMount() {
    this.fetchExchangeRates();
    this.updateExchangeRatesInterval = setInterval(this.fetchExchangeRates, 5 * 60 * 1000);
  }

  componentWillUnmount() {
    clearInterval(this.updateExchangeRatesInterval);
  }

  fetchExchangeRates = () => {
    const apiUrlEuroToBRL = 'https://economia.awesomeapi.com.br/last/EUR-BRL';
    const apiUrlBRLToEuro = 'https://economia.awesomeapi.com.br/last/BRL-EUR';

    Promise.all([axios.get(apiUrlEuroToBRL), axios.get(apiUrlBRLToEuro)])
      .then((responses) => {
        const exchangeRateEuroToBRL = (parseFloat(responses[0].data['EURBRL'].bid) - 0.0).toFixed(2);
        const exchangeRateBRLToEuro = (parseFloat(responses[1].data['BRLEUR'].bid) - 0.0).toFixed(2);

        if (!isNaN(exchangeRateEuroToBRL) && !isNaN(exchangeRateBRLToEuro)) {
          this.setState({ exchangeRateEuroToBRL, exchangeRateBRLToEuro });
        } else {
          console.error('Invalid exchange rate:', responses);
        }
      })
      .catch((error) => {
        console.error('Error fetching exchange rates:', error);
      });
  };

  handleInput1 = (event) => {
    const inputValue1WithComma = event.target.value;
    const inputValue1 = inputValue1WithComma.replace(',', '.');
    const calculaValue = inputValue1 * 0.03;
    const calculaCosts = inputValue1 - calculaValue;
    const selecte1 = this.state.selecte1;
    const exchangeRate = selecte1 === 'EUR' ? this.state.exchangeRateEuroToBRL : this.state.exchangeRateBRLToEuro;
    const inputValue2 = (calculaCosts * exchangeRate).toFixed(2).replace('.', ',');
    this.setState({ inputValue1: inputValue1WithComma, inputValue2, calculaValue, calculaCosts });
  };
  handleInput2 = (event) => {
    const inputValue2WithComma = event.target.value;
    const inputValue2 = inputValue2WithComma.replace(',', '.');
    const calculaValue = inputValue2 * 0.03;
    const valueTaxa = calculaValue / 2;
    const calculaCosts = inputValue2 - calculaValue;
    const selecte1 = this.state.selecte1;
    const exchangeRate = selecte1 === 'EUR' ? this.state.exchangeRateEuroToBRL : this.state.exchangeRateBRLToEuro;
    const inputValue1 = (inputValue2 / exchangeRate).toFixed(2).replace('.', ',');
    this.setState({ inputValue2: inputValue2WithComma, inputValue1, valueTaxa, calculaValue, calculaCosts });
  };
  handleSelecte1 = (event) => {
    const selecte1 = event.target.value;
    const selecte2 = selecte1 === 'EUR' ? 'BRL' : 'EUR';

    this.setState({ selecte1, selecte2 }, this.calculaInputValue2);
  };

  handleSelecte2 = (event) => {
    const selecte2 = event.target.value;
    const selecte1 = selecte2 === 'BRL' ? 'EUR' : 'BRL';
    this.setState({ selecte2, selecte1 }, this.calculaInputValue2);
  };

  calculaInputValue1 = () => {
    const { inputValue2, selecte1, exchangeRateEuroToBRL, exchangeRateBRLToEuro } = this.state;
    const exchangeRate = selecte1 === 'EUR' ? exchangeRateEuroToBRL : exchangeRateBRLToEuro;
    const calculaValue = parseFloat(inputValue2.replace(',', '.')) * 0.03;
    const calculaCosts = parseFloat(inputValue2.replace(',', '.')) - calculaValue;
    const inputValue1 = (calculaCosts / exchangeRate).toFixed(2).replace('.', ',');

    this.setState({ inputValue1, calculaValue, calculaCosts });
  };

  calculaInputValue2 = () => {
    const { inputValue1, selecte1, exchangeRateEuroToBRL, exchangeRateBRLToEuro } = this.state;
    const exchangeRate = selecte1 === 'EUR' ? exchangeRateEuroToBRL : exchangeRateBRLToEuro;
    const calculaValue = parseFloat(inputValue1.replace(',', '.')) * 0.03;
    const calculaCosts = parseFloat(inputValue1.replace(',', '.')) - calculaValue;
    const inputValue2 = (calculaCosts * exchangeRate).toFixed(2).replace('.', ',');

    this.setState({ inputValue2, calculaValue, calculaCosts });
  };

  render() {
    const { classes, t } = this.props;
    const oppositeCurrency = this.state.selecte1 === 'BRL' ? 'BRL' : 'EUR';

    const operationCosts = `${this.state.calculaValue.toFixed(2).replace('.', ',')} ${oppositeCurrency}`;
    const convertedValue = `${this.state.calculaCosts.toFixed(2).replace('.', ',')} ${oppositeCurrency}`;
    const exchangeRate = oppositeCurrency === 'BRL' ? `${this.state.exchangeRateBRLToEuro} EUR` : `${this.state.exchangeRateEuroToBRL} BRL`;
    const taxa = parseFloat(exchangeRate).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
    const exchangeDirection = this.state.selecte1 === 'BRL' ? `REAL ${t('cambio.card')} EURO` : `EURO ${t('cambio.card')} REAL`;

    return (
      <>
        <Header />
        <div className={classes.bar}>
          <Container>
            <Typography component='h1'>{t('cambio.title')}</Typography>
            <div
              role='presentation'
              onClick={(e) => {
                setTimeout(() => {
                  window.scrollTo(0, 2150);
                }, 100);
                this.props.history.push('/');
              }}
            >
              <Breadcrumbs aria-label='breadcrumb' className={classes.barLink}>
                <Link underline='hover' color='inherit' href='/'>
                  {t('cambio.bar1')}
                </Link>
                <Link underline='hover' color='inherit' href='/envio-brasil'>
                  {t('cambio.bar2')}
                </Link>
              </Breadcrumbs>
            </div>
          </Container>
        </div>

        <Grid container className={classes.banner1}>
          <Container>
            <Grid className={classes.banner}>
              <div className={classes.rectangle}>
                <Typography component='h2'>{t('cambio.subTitle')}</Typography>
                <Typography component='h3'>
                  {t('cambio.title1')} {exchangeDirection}
                </Typography>

                <ExchangeInput
                  label={t('cambio.titleMoeda')}
                  placeholder='0,00'
                  value={this.state.inputValue1}
                  onChange={(e) => this.handleInput1(e)}
                  options={[
                    { value: 'EUR', label: 'EUR' },
                    { value: 'BRL', label: 'BRL' },
                  ]}
                  selectValue={this.state.selecte1}
                  selectOnChange={(e) => this.handleSelecte1(e)}
                />

                <div className={classes.divPrice}>
                  <p className={classes.calcLabelStyle}>
                    <ion-icon name='close-outline' className={classes.iconStyle}>
                      -
                    </ion-icon>
                    <b className={classes.valueStyle} id='operationCosts'>
                      {operationCosts}
                    </b>
                    <span className={classes.descStyle}>{t('cambio.card1')}</span>
                  </p>
                  <p className={classes.calcLabelStyle}>
                    <ion-icon name='close-outline' className={classes.iconStyle}>
                      =
                    </ion-icon>
                    <b className={classes.valueStyle} id='convertedValue'>
                      {convertedValue}
                    </b>
                    <span className={classes.descStyle}>{t('cambio.card2')}</span>
                  </p>
                  <p className={classes.calcLabelStyle}>
                    <ion-icon name='close-outline' className={classes.iconStyle}>
                      x
                    </ion-icon>
                    <b className={classes.valueStyle} id='exchangeRate'>
                      {taxa}
                    </b>
                    <span className={classes.descStyle}> {t('cambio.card3')}</span>
                  </p>
                </div>

                <ExchangeInput
                  label={t('cambio.subTitleMoeda')}
                  value={this.state.inputValue2}
                  placeholder='0,00'
                  onChange={(e) => this.handleInput2(e)}
                  options={[
                    { value: 'EUR', label: 'EUR' },
                    { value: 'BRL', label: 'BRL' },
                  ]}
                  selectValue={this.state.selecte2}
                  selectOnChange={(e) => this.handleSelecte2(e)}
                />
              </div>
              <div item xs={6} className={classes.image}>
                <img src={bannerMoeda} alt={t('tag.map')} title={t('tag.map')} className={classes.StyImage} />
              </div>
            </Grid>
          </Container>
        </Grid>
        <Footer />
      </>
    );
  }
}

export default withRouter(withStyles(styles)(withTranslation()(Cambio)));
