export const APP_SETTINGS = JSON.parse(localStorage.getItem('APP_SETTINGS')) !== null ? JSON.parse(localStorage.getItem('APP_SETTINGS')) : [{}];

const stylePage = (theme) => ({
  formGroup: {
    position: 'relative',
    display: 'block',
    marginBottom: '1rem',
    width: '100%',
  },
  label: {
    display: 'block',
    color: '#555555',
    fontSize: '0.75rem',
    fontWeight: '500',
    textTransform: 'uppercase',
    marginBottom: '.5rem',
  },
  inputGroup: {
    display: 'flex',
    flexFlow: 'row nowrap',
    alignItems: 'stretch',
  },
  inputGroup__input: {
    height: '3rem',
    flexGrow: '1',
    border: 'solid 2px #DDD7D7',
    borderRadius: '0',
    boxSizing: 'border-box',
    '& input': {
      display: 'flex',
      alignItems: 'center',
      padding: '0 1rem',
      height: '3rem',
      fontSize: '1.5rem',
      fontWeight: '500',
    },
  },
  inputGroup__select: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '0 1rem',
    height: '3rem',
    background: '#DDD7D7',
    border: 'none',
    borderRadius: '0',
    fontSize: '1rem',
    fontWeight: '500',
    cursor: 'pointer',
  },
});

export default stylePage;
