import React from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles, Input } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import stylePage from './style';

class ExchangeInput extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      disabled: this.props.disabled || false,
    };
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes.formGroup}>
        <label className={classes.label}>{this.props.label}</label>

        <div className={classes.inputGroup}>
          <Input
            inputProps={{ inputMode: 'numeric', pattern: '[0-9]*[,]?[0-9]*', maxLength: 20 }}
            variant='outlined'
            margin='normal'
            placeholder={this.props.placeholder}
            type={'text'}
            className={classes.inputGroup__input}
            disableUnderline={true}
            value={this.props.value}
            onChange={(e) => {
              const numericValue = e.target.value.replace(/[^0-9.]/g, '');
              if (numericValue !== e.target.value) {
                e.target.value = numericValue;
                this.props.onChange(e);
              } else {
                this.props.onChange(e);
              }
            }}
            readOnly={this.state.disabled}
          />

          <select className={classes.inputGroup__select} value={this.props.selectValue} onChange={this.props.selectOnChange}>
            {this.props.options.map((item, key) => {
              return (
                <option value={item.value} key={key}>
                  {item.label}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    );
  }
}

export default withRouter(withStyles(stylePage)(withTranslation()(ExchangeInput)));
