import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Button, makeStyles } from '@material-ui/core';
import { Spinner } from '../modules';

const sizes = {
  lg: {
    height: '3rem',
    fontSize: '1.5rem',
  },
  md: {
    height: '2.5rem',
    fontSize: '1rem',
  },
  sm: {
    height: '2rem',
    fontSize: '.75rem',
  },
  ref: {
    height: '2rem',
    fontSize: '1rem',
  },
};

function StandardButton(props) {
  const size = props.size || 'md';

  const useStyle = makeStyles((theme) => ({
    button: {
      paddingLeft: '2rem',
      paddingRight: '2rem',
      background: 'linear-gradient(85.42deg, #FD8246 70%, #FFE4D7 100%)',
      backgroundSize: '100% 100%',
      backgroundPosition: '0 0',
      backgroundRepeat: 'no-repeat',
      color: '#FFF',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      height: sizes[size].height,
      fontSize: sizes[size].fontSize,
      opacity: props.await !== undefined && props.await ? '0.5' : '1',
    },
  }));

  const classes = useStyle();

  return (
    <Button type={props.type || 'button'} variant='contained' className={`${props.style} ${classes.button}`} onClick={props.onClick}>
      {props.await !== undefined && props.await ? <Spinner /> : props.children}
    </Button>
  );
}

function PrimaryButton(props) {
  const { text, width, background, fontSize, onClick, color, alt, title, border, borderRadius } = props;

  const sizeStyles = sizes[fontSize] || {};
  const [hoverede, setHoverede] = useState(false);

  const buttonStyle = {
    background: background || ' linear-gradient(85.42deg, #FD8246 40%, #FFE4D7 150%)',
    backgroundSize: '100% 100%',
    backgroundPosition: '0 0',
    backgroundRepeat: 'no-repeat',
    color: color || '#FFFFFF',
    fontWeight: '500',
    cursor: 'pointer',
    border: border || '1px solid #E95810',
    textDecoration: 'none',
    transition: '0.28s ease',
    padding: '0px',
    borderRadius: borderRadius || '5px',
    width: width || '150px',
    display: 'flex',
    flexFlow: 'row wrap',
    alignItems: 'center',
    textAlign: 'center',
    justifyContent: 'center',
    outline: 'none',
    fontFamily: '"Poppins", Sans-serif',
    height: sizeStyles.height || '48px',
    fontSize: sizeStyles.fontSize || '20px',
    '&.MuiDisabled': {
      background: '#ddd',
    },
  };

  return (
    <button
      className='button'
      style={{
        ...buttonStyle,
        background: hoverede ? 'linear-gradient(85.42deg, #FD8246 0%, #FFE4D7 20%)' : buttonStyle.background,
        backgroundSize: '100% 100%',
        backgroundPosition: '0 0',
        backgroundRepeat: 'no-repeat',
        color: hoverede ? '#E95810' : buttonStyle.color,
      }}
      onMouseEnter={() => setHoverede(true)}
      onMouseLeave={() => setHoverede(false)}
      onClick={onClick}
      alt={alt}
      title={title}
    >
      {text}
    </button>
  );
}

PrimaryButton.propTypes = {
  text: PropTypes.string.isRequired,
  background: PropTypes.string,
  width: PropTypes.string,
  borderRadius: PropTypes.string,
  border: PropTypes.string,
  color: PropTypes.string,
  fontSize: PropTypes.oneOf(['lg', 'md', 'sm', 'ref']),
  onClick: PropTypes.func,
};

function SecondButton(props) {
  const { text, width, background, fontSize, onClick, border, color, alt, title } = props;

  const sizeStyles = sizes[fontSize] || {};
  const [hovered, setHovered] = useState(false);

  const buttonStyle1 = {
    background: background || 'transparent',
    color: color || ' #E95810',
    border: border || '1px solid #E95810',
    fontWeight: '500',
    textDecoration: 'none',
    textAlign: 'center',
    cursor: 'pointer',
    justifyContent: 'center',
    transition: '0.28s ease',
    padding: '0px',
    borderRadius: '15px',
    width: width || '150px',
    display: 'flex',
    flexFlow: 'row wrap',
    alignItems: 'center',
    fontFamily: '"Poppins", Sans-serif',
    height: sizeStyles.height || '48px',
    fontSize: sizeStyles.fontSize || '16px',
  };

  return (
    <button
      className='button'
      style={{
        ...buttonStyle1,
        background: hovered ? 'linear-gradient(85.42deg, #FD8246 0%, #FFE4D7 20%)' : buttonStyle1.background,
        backgroundSize: '100% 100%',
        backgroundPosition: '0 0',
        backgroundRepeat: 'no-repeat',
        color: hovered ? '#E95810' : buttonStyle1.color,
      }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={onClick}
      alt={alt}
      title={title}
    >
      {text}
    </button>
  );
}

SecondButton.propTypes = {
  text: PropTypes.string.isRequired,
  background: PropTypes.string,
  border: PropTypes.string,
  width: PropTypes.string,
  color: PropTypes.string,
  fontSize: PropTypes.oneOf(['lg', 'md', 'sm', 'ref']),
  onClick: PropTypes.func,
};
function ButtonVerifica(props) {
  const { text, width, background, fontSize, onClick, color, alt, title, border, borderRadius, disabled } = props;

  const sizeStyles = sizes[fontSize] || {};
  const [hoverede, setHoverede] = useState(false);

  const buttonStyle = {
    background: background || ' linear-gradient(85.42deg, #FD8246 40%, #FFE4D7 150%)',
    backgroundSize: '100% 100%',
    backgroundPosition: '0 0',
    backgroundRepeat: 'no-repeat',
    color: color || '#FFFFFF',
    fontWeight: '500',
    cursor: 'pointer',
    border: border || '1px solid #E95810',
    textDecoration: 'none',
    transition: '0.28s ease',
    padding: '0px',
    borderRadius: borderRadius || '5px',
    width: width || '150px',
    display: 'flex',
    flexFlow: 'row wrap',
    alignItems: 'center',
    textAlign: 'center',
    justifyContent: 'center',
    outline: 'none',
    fontFamily: '"Poppins", Sans-serif',
    height: sizeStyles.height || '48px',
    fontSize: sizeStyles.fontSize || '20px',
    '&.Mui-disabled': {
      background: '#ddd',
      cursor: 'not-allowed',
    },
  };

  return (
    <button
      className='button'
      style={{
        ...buttonStyle,
        background: disabled ? '#ddd' : buttonStyle.background,
        backgroundSize: '100% 100%',
        backgroundPosition: '0 0',
        backgroundRepeat: 'no-repeat',
        color: buttonStyle.color,
      }}
      onMouseEnter={() => setHoverede(true)}
      onMouseLeave={() => setHoverede(false)}
      onClick={onClick}
      alt={alt}
      title={title}
      disabled={disabled}
    >
      {text}
    </button>
  );
}

ButtonVerifica.propTypes = {
  text: PropTypes.string.isRequired,
  background: PropTypes.string,
  width: PropTypes.string,
  borderRadius: PropTypes.string,
  border: PropTypes.string,
  color: PropTypes.string,
  fontSize: PropTypes.oneOf(['lg', 'md', 'sm', 'ref']),
  onClick: PropTypes.func,
  disabled: PropTypes.bool,
};

export { StandardButton, PrimaryButton, SecondButton, ButtonVerifica };
