import React from 'react';
import { InputLabel, Select, FormLabel, MenuItem, makeStyles, Input, FormControlLabel, Checkbox, Typography } from '@material-ui/core';

function generateRandString() {
  return (Math.random() + 1).toString(36).substring(2);
}

const useStyles = makeStyles((theme) => ({
  input: (props) => ({
    borderRadius: '0.5rem',
    width: props.width || '100%',
    height: props.height || '3rem',
    background: props.background || '#fff', // Cor de fundo padrão é branco
    paddingLeft: '1rem',
    margin: '0 auto',
    border: props.border || '1px solid #000', // Borda padrão é preta
    [theme.breakpoints.down('md')]: {
      width: '90%',
    },
  }),
}));

function InputPrestamo({ placeholder, value, onChange, classe, type, width, height, background, border }) {
  const classes = useStyles({ width, height, background, border });

  return <Input placeholder={placeholder} type={type} disableUnderline={true} required className={classes.input} value={value} onChange={onChange} variant='outlined' />;
}

export { InputPrestamo };
