export const APP_SETTINGS = JSON.parse(localStorage.getItem('APP_SETTINGS')) !== null ? JSON.parse(localStorage.getItem('APP_SETTINGS')) : [{}];

const stylePage = (theme) => ({
  container: {
    width: '100%',
    marginRight: 'auto',
    marginLeft: 'auto',
    maxWidth: '90%',
    [theme.breakpoints.up(1700)]: {
      maxWidth: '80%',
    },
    [theme.breakpoints.up(2000)]: {
      maxWidth: '60%',
    },
  },
  //Location

  cardTitle: {
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'transparent',
    '& h1': {
      color: '#1A1A1A',
      fontSize: '1.5rem',
      fontWeight: '600',
      margin: 'auto',
      [theme.breakpoints.down('md')]: {
        fontSize: '2rem',
      },
    },
    [theme.breakpoints.down('md')]: {
      gridTemplateColumns: '100%',
      minHeight: '50px',
    },
  },

  cardInputs: {
    background: '#FFF',
    width: '100%',
    display: 'flex',
    justifyContent: 'space-evenly',
    alignItems: 'center',
    marginBottom: '1rem',
    '& h2': {
      color: '#1A1A1A',
      fontSize: '24px',
      fontWeight: '700',
      lineHeight: '28px',
      margin: '0.5rem 0',
    },
    '& h3': {
      color: '#555555',
      fontSize: '16px',
      fontWeight: '400',
      lineHeight: '20px',
      margin: '0.5rem 0',
    },
    [theme.breakpoints.down('md')]: {
      gridTemplateColumns: '100%',
      placeItems: 'center',
      width: '100%',
    },
  },
  maps: {
    width: '600',
    height: '300',
    border: '0',
    background: 'transparent',
    [theme.breakpoints.down('md')]: {
      height: '90vw',
      width: '90vw',
      objectFit: 'cover',
      objectPosition: 'top right',
    },
  },

  imageLocation: {
    height: '5rem',
    width: '5rem',
  },
});
export default stylePage;
