import { colors } from '../../configs';

const stylePage = (theme) => ({
  listContainer: {
    position: 'relative',
    display: 'flex',
    flexFlow: 'row nowrap',
    alignItems: 'center',
  },
  listRow: {
    width: '100%',
    overflowX: 'scroll',
    display: 'flex',
    flexFlow: 'row nowrap',
    alignItems: 'center',
    gap: '1rem',
    padding: '1rem 0',
    scrollSnapType: 'x mandatory',
    scrollbarWidth: 'none',
    scrollBehavior: 'smooth',
    '& > *': {
      scrollSnapAlign: 'start',
    },
    '&::-webkit-scrollbar': {
      display: 'none',
    },
  },
  action: {
    height: '4rem',
    width: '4rem',
    padding: '0',
    border: 'none',
    opacity: '.5',
    cursor: 'pointer',
    background: 'transparent',
    '&:hover': {
      opacity: '1',
    },
    [theme.breakpoints.down('sm')]: {
      display: 'none',
    },
  },
  criptoCard: {
    display: 'flex',
    flexFlow: 'row nowrap',
    alignItems: 'center',
    padding: '.75rem .75rem',
    background: '#fff',
    boxShadow: '2px 2px 8px rgba(0,0,0,0.1)',
  },
  criptoCard__info: {
    marginLeft: '.5rem',
  },
  criptoCard__info__symbol: {
    whiteSpace: 'nowrap',
    fontSize: '.75rem',
    fontWeight: '500',
    color: colors.lightBlack,
    textTransform: 'uppercase',
    margin: '0',
  },
  criptoCard__info__value: {
    whiteSpace: 'nowrap',
    fontSize: '1rem',
    fontWeight: '600',
    color: colors.black,
    margin: '0',
  },
  icon: {
    width: '2rem',
    height: '2rem',
  },
});

export default stylePage;
