import React from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import stylePage from './style';
import { ChevronLeft, ChevronRight } from '@material-ui/icons';
import { Binance, Bitcoin, Busd, Dogecoin, Ethereum, Eur, GbtcToken, Litecoin, Polkadot, Tron, Usd, Usdt, VeloxToken, Xrp, Sol, Ada } from '../icons';
import { colors } from '../../configs';
import binanceService from '../../services/binance.service';

class CryptoList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      cryptoList: [],
    };
  }

  componentDidMount() {
    binanceService
      .getPrices()
      .then((res) => {
        const list = [
          {
            symbol: 'BTC',
            value: res.data.binance['BTCEUR'],
          },
          {
            symbol: 'ETH',
            value: res.data.binance['ETHEUR'],
          },
          {
            symbol: 'DOGE',
            value: res.data.binance['DOGEEUR'],
          },
          {
            symbol: 'DOT',
            value: res.data.binance['DOTEUR'],
          },
          {
            symbol: 'LTC',
            value: res.data.binance['LTCEUR'],
          },
          {
            symbol: 'ADA',
            value: res.data.binance['ADAEUR'],
          },
          {
            symbol: 'BNB',
            value: res.data.binance['BNBEUR'],
          },
          {
            symbol: 'XRP',
            value: res.data.binance['XRPEUR'],
          },
          {
            symbol: 'SOL',
            value: res.data.binance['SOLEUR'],
          },
          {
            symbol: 'TRON',
            value: res.data.binance['TRXEUR'],
          },
        ];
        this.setState({ cryptoList: list });
      })
      .catch((error) => {
        //console.log(error);
      });
  }

  handleScroll = (direction) => {
    let element = this.element;
    let currentPosition = element.scrollLeft;
    let maxScroll = element.offsetWidth - 128;

    let newPosition = currentPosition + maxScroll;
    if (direction === 'left') {
      newPosition = currentPosition - maxScroll;
    }

    element.scrollTo(newPosition, 0);
  };

  render() {
    const { classes } = this.props;

    function CryptoIcon(props) {
      let icon;
      switch (props.symbol) {
        case 'BTC':
          icon = <Bitcoin className={classes.icon} fill={colors.orange} />;
          break;
        case 'USDT':
          icon = <Usdt className={classes.icon} fill={colors.orange} />;
          break;
        case 'EUR':
          icon = <Eur className={classes.icon} fill={colors.orange} />;
          break;
        case 'USD':
          icon = <Usd className={classes.icon} fill={colors.orange} />;
          break;
        case 'BNB':
          icon = <Binance className={classes.icon} fill='#f1bb13' />;
          break;
        case 'DOGE':
          icon = <Dogecoin className={classes.icon} fill='#BEA23D' />;
          break;
        case 'ETH':
          icon = <Ethereum className={classes.icon} fill='#517be8' />;
          break;
        case 'LTC':
          icon = <Litecoin className={classes.icon} fill='#3A62A0' />;
          break;
        case 'TRON':
          icon = <Tron className={classes.icon} fill='#FE0111' />;
          break;
        case 'VCL':
          icon = <VeloxToken className={classes.icon} fill={colors.orange} />;
          break;
        case 'DOT':
          icon = <Polkadot className={classes.icon} fill='#e7087e' />;
          break;
        case 'BUSD':
          icon = <Busd className={classes.icon} fill={colors.orange} />;
          break;
        case 'GBTC':
          icon = <GbtcToken className={classes.icon} fill={colors.orange} />;
          break;
        case 'XRP':
          icon = <Xrp className={classes.icon} fill='#0a5ba2' />;
          break;
        case 'SOL':
          icon = <Sol className={classes.icon} fill='#9a4afe' />;
          break;
        case 'ADA':
          icon = <Ada className={classes.icon} fill='#0838B0' />;
          break;
        default:
          icon = <Bitcoin className={classes.icon} fill={colors.orange} />;
          break;
      }

      return icon;
    }

    const CriptoCard = (props) => {
      return (
        <div className={classes.criptoCard}>
          <CryptoIcon symbol={props.symbol} size='24' />
          <div className={classes.criptoCard__info}>
            <p className={classes.criptoCard__info__symbol}>{props.symbol}</p>
            <p className={classes.criptoCard__info__value}>â‚¬ {props.value}</p>
          </div>
        </div>
      );
    };

    return (
      <div className={classes.listContainer}>
        <button type='button' className={`${classes.action} ${classes.leftAction}`} onClick={() => this.handleScroll('left')}>
          <ChevronLeft fontSize='large' />
        </button>
        <div
          className={classes.listRow}
          ref={(el) => {
            this.element = el;
          }}
        >
          {this.state.cryptoList.map((item, key) => (
            <CriptoCard key={key} symbol={item.symbol} value={item.value} />
          ))}
        </div>
        <button type='button' className={`${classes.action} ${classes.rightAction}`} onClick={() => this.handleScroll('right')}>
          <ChevronRight fontSize='large' />
        </button>
      </div>
    );
  }
}

export default withRouter(withStyles(stylePage)(withTranslation()(CryptoList)));
