import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Button, makeStyles } from '@material-ui/core';
import { Spinner } from '../modules';

function PrimaryButton(props) {
  const { text, width, background, fontSize, onClick, color, alt, title, border, borderRadius } = props;

  const sizeStyles = sizes[fontSize] || {};
  const [hoverede, setHoverede] = useState(false);

  const buttonStyle = {
    background: background || ' linear-gradient(85.42deg, #FD8246 40%, #FFE4D7 150%)',
    backgroundSize: '100% 100%',
    backgroundPosition: '0 0',
    backgroundRepeat: 'no-repeat',
    color: color || '#FFFFFF',
    fontWeight: '500',
    cursor: 'pointer',
    border: border || '1px solid #E95810',
    textDecoration: 'none',
    transition: '0.28s ease',
    padding: '0px',
    borderRadius: borderRadius || '5px',
    width: width || '150px',
    display: 'flex',
    flexFlow: 'row wrap',
    alignItems: 'center',
    textAlign: 'center',
    justifyContent: 'center',
    outline: 'none',
    fontFamily: '"Poppins", Sans-serif',
    height: sizeStyles.height || '48px',
    fontSize: sizeStyles.fontSize || '20px',
    '&.MuiDisabled': {
      background: '#ddd',
    },
  };

  return (
    <button
      className='button'
      style={{
        ...buttonStyle,
        background: hoverede ? 'linear-gradient(85.42deg, #FD8246 0%, #FFE4D7 20%)' : buttonStyle.background,
        backgroundSize: '100% 100%',
        backgroundPosition: '0 0',
        backgroundRepeat: 'no-repeat',
        color: hoverede ? '#E95810' : buttonStyle.color,
      }}
      onMouseEnter={() => setHoverede(true)}
      onMouseLeave={() => setHoverede(false)}
      onClick={onClick}
      alt={alt}
      title={title}
    >
      {text}
    </button>
  );
}

PrimaryButton.propTypes = {
  text: PropTypes.string.isRequired,
  background: PropTypes.string,
  width: PropTypes.string,
  borderRadius: PropTypes.string,
  border: PropTypes.string,
  color: PropTypes.string,
  fontSize: PropTypes.oneOf(['lg', 'md', 'sm', 'ref']),
  onClick: PropTypes.func,
};

export default PrimaryButton;
