import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles, Grid, Typography, Container, Breadcrumbs, Link, Tabs, Tab } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import styles from './style';
import Header from '../../components/header';
import { bannerFundoCrypto } from '../../components/icons';
import Footer from '../../components/footer';
import ExchangeInput from '../../components/exchangeInput';
import binanceService from '../../services/binance.service';

class CambioCripto extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue1: '',
      inputValue2: '',
      select1: 'BTC',
      select2: 'EUR',
      costPercentage: 0.05,
      operationCosts: 0.0,
      convertedValue: 0.0,
      cryptos: [],
      exchangeRate: '',
      tabValue: 0,
    };
  }

  componentDidMount() {
    binanceService
      .getPrices()
      .then((res) => {
        this.setState({ cryptos: res.data.binance });
        const { select1, select2 } = this.state;
        let currentExchange = res.data.binance[`${select1}${select2}`];
        this.setState({ exchangeRate: currentExchange });
      })
      .catch((error) => {
        //console.log(error);
      });
  }

  handleInput1 = (event) => {
    const { select1, select2 } = this.state;

    const inputValue1 = event.target.value.replace(',', ',');

    if (isNaN(inputValue1) || isNaN(this.state.exchangeRate)) {
      return;
    }

    console.log('Debug handleInput1:', inputValue1, select1, select2, this.state.exchangeRate);

    this.setState({ inputValue1 });
    this.calculaInputValue2(inputValue1, select1, select2);
  };

  handleSelect1 = (event) => {
    const { inputValue1, select2 } = this.state;

    this.setState({ select1: event.target.value });
    this.calculaInputValue2(inputValue1, event.target.value, select2);
  };

  handleSelect2 = (event) => {
    const { select1, inputValue1 } = this.state;

    this.setState({ select2: event.target.value });
    this.calculaInputValue2(inputValue1, select1, event.target.value);
  };

  calculaInputValue2 = (newValue, select1, select2) => {
    let currentExchange;
    if (select1 === 'USDT') {
      currentExchange = this.state.cryptos['BTCEUR'] / this.state.cryptos['BTCUSDT'];
    } else {
      currentExchange = this.state.cryptos[`${select1}${select2}`];
    }
    const operationCosts = newValue * this.state.costPercentage;
    const convertedValue = newValue - operationCosts;
    const output = (convertedValue * currentExchange).toFixed(2).replace('.', ',');

    this.setState({
      inputValue2: output,
      exchangeRate: currentExchange,
      operationCosts: parseFloat(operationCosts).toFixed(2),
      convertedValue: parseFloat(convertedValue).toFixed(2),
    });
  };
  calculaInputValue1 = (newValue, select1, select2) => {
    let currentExchange;
    if (select1 === 'USDT') {
      currentExchange = this.state.cryptos['BTCEUR'] / this.state.cryptos['BTCUSDT'];
    } else {
      currentExchange = this.state.cryptos[`${select1}${select2}`];
    }
    const operationCosts = newValue * this.state.costPercentage;
    const convertedValue = newValue - operationCosts;
    const output = (convertedValue / currentExchange).toFixed(2).replace('.', ',');

    this.setState({
      inputValue1: output,
      exchangeRate: currentExchange,
      operationCosts: parseFloat(operationCosts).toFixed(2),
      convertedValue: parseFloat(convertedValue).toFixed(2),
    });
  };

  handleInput2 = (event) => {
    const { select1, select2 } = this.state;

    const inputValue2 = event.target.value.replace(',', ',');

    if (isNaN(inputValue2)) {
      return;
    }

    this.setState({ inputValue2 });
    this.calculaInputValue1(inputValue2, select1, select2);
  };
  buySell = (value) => {
    let operationCosts = 0.05;
    if (value) {
      operationCosts = 0.03;
    }

    this.setState({
      tabValue: value,
      costPercentage: operationCosts,
      inputValue1: '',
      inputValue2: '',
      select1: 'BTC',
      operationCosts: 0.0,
      convertedValue: 0.0,
    });
  };

  render() {
    const { classes, t } = this.props;

    function tabsProps(index) {
      return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
      };
    }

    return (
      <>
        <Header />
        <div className={classes.bar}>
          <Container>
            <Typography component='h1'>{t('cambioCrypto.title')}</Typography>
            <div
              role='presentation'
              onClick={(e) => {
                setTimeout(() => {
                  window.scrollTo(0, 2150);
                }, 100);
                this.props.history.push('/');
              }}
            >
              <Breadcrumbs aria-label='breadcrumb' className={classes.barLink}>
                <Link underline='hover' color='inherit' href='/'>
                  {t('cambioCrypto.bar1')}
                </Link>
                <Link underline='hover' color='inherit' href='/cambio-crypto'>
                  {t('cambioCrypto.bar2')}
                </Link>
              </Breadcrumbs>
            </div>
          </Container>
        </div>

        <Grid container className={classes.banner1}>
          <Container>
            <Grid className={classes.banner}>
              <div className={classes.rectangle}>
                <Typography component='h2'>{t('cambio.subTitle')}</Typography>
                <Typography component='h3'>{`${t('cambio.title1')} ${this.state.select1} ${t('cambio.card')} ${this.state.select2}`}</Typography>

                <Tabs value={this.state.tabValue} onChange={(event, value) => this.buySell(value)} aria-label='basic tabs example' className={classes.tabsContainer}>
                  <Tab label='Compra' {...tabsProps(0)} />
                  <Tab label='Venta' {...tabsProps(1)} />
                </Tabs>

                <ExchangeInput
                  label={this.state.tabValue ? t('cambioCrypto.inSellLabel') : t('cambioCrypto.inBuyLabel')}
                  placeholder='0,00'
                  value={this.state.inputValue1}
                  onChange={(e) => this.handleInput1(e)}
                  options={[
                    { value: 'BTC', label: 'BTC' },
                    { value: 'ETH', label: 'ETH' },
                    { value: 'USDT', label: 'USDT' },
                  ]}
                  selectValue={this.state.select1}
                  selectOnChange={(e) => this.handleSelect1(e)}
                />

                <div className={classes.divPrice}>
                  <p className={classes.calcLabelStyle}>
                    <ion-icon name='close-outline' className={classes.iconStyle}>
                      -
                    </ion-icon>
                    <b className={classes.valueStyle}>
                      {this.state.operationCosts} {this.state.select1}
                    </b>
                    <span className={classes.descStyle}>{t('cambio.card1')}</span>
                  </p>
                  <p className={classes.calcLabelStyle}>
                    <ion-icon name='close-outline' className={classes.iconStyle}>
                      =
                    </ion-icon>
                    <b className={classes.valueStyle}>
                      {this.state.convertedValue} {this.state.select1}
                    </b>
                    <span className={classes.descStyle}>{t('cambio.card2')}</span>
                  </p>
                  <p className={classes.calcLabelStyle}>
                    <ion-icon name='close-outline' className={classes.iconStyle}>
                      x
                    </ion-icon>
                    <b className={classes.valueStyle}>{this.state.exchangeRate}</b>
                    <span className={classes.descStyle}> {t('cambio.card3')}</span>
                  </p>
                </div>

                <ExchangeInput
                  label={this.state.tabValue ? t('cambioCrypto.outSellLabel') : t('cambioCrypto.outBuyLabel')}
                  placeholder='0,00'
                  value={this.state.inputValue2}
                  onChange={(e) => this.handleInput2(e)}
                  options={[{ value: 'EUR', label: 'EUR' }]}
                  selectValue={this.state.select2}
                  selectOnChange={(e) => this.handleSelect2(e)}
                />
              </div>
              <div className={classes.image}>
                <img src={bannerFundoCrypto} alt={t('tag.map')} title={t('tag.map')} className={classes.StyImage} />
              </div>
            </Grid>
          </Container>
        </Grid>
        <Footer />
      </>
    );
  }
}

export default withRouter(withStyles(styles)(withTranslation()(CambioCripto)));
