import React from 'react';
import { withRouter } from 'react-router-dom';
import { withStyles, Input } from '@material-ui/core';
import { withTranslation } from 'react-i18next';
import stylePage from './style';

class ExchangeInput extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      disabled: this.props.disabled || false,
    };
  }
  handleInputChange = (e) => {
    const input = e.target.value;

    // Remova caracteres não numéricos, exceto a vírgula
    const numericInput = input.replace(/[^\d,]/g, '');

    // Divida a entrada em parte inteira e decimal
    const [integerPart, decimalPart] = numericInput.split(',');

    // Limite a parte decimal a 2 dígitos
    const limitedDecimalPart = decimalPart ? decimalPart.slice(0, 2) : '';

    // Combine a parte inteira e a parte decimal limitada, mantendo a vírgula
    const normalizedValue = `${integerPart || ''}${limitedDecimalPart !== '' ? ',' : ''}${limitedDecimalPart}`;

    // Verifique se a última entrada é uma vírgula e se não é, atualize o valor
    if (input.charAt(input.length - 1) === ',' && limitedDecimalPart === '') {
      this.props.onChange({
        target: {
          value: normalizedValue + ',',
        },
      });
    } else {
      this.props.onChange({
        target: {
          value: normalizedValue,
        },
      });
    }
  };

  render() {
    const { classes } = this.props;

    return (
      <div className={classes.formGroup}>
        <label className={classes.label}>{this.props.label}</label>

        <div className={classes.inputGroup}>
          <Input
            inputProps={{ inputMode: 'numeric', pattern: '[0-9]*[,]?[0-9]*', maxLength: 20 }}
            variant='outlined'
            margin='normal'
            placeholder={this.props.placeholder}
            type={'text'}
            className={classes.inputGroup__input}
            disableUnderline={true}
            value={this.props.value}
            onChange={this.handleInputChange}
            readOnly={this.state.disabled}
          />

          <select className={classes.inputGroup__select} value={this.props.selectValue} onChange={this.props.selectOnChange}>
            {this.props.options.map((item, key) => {
              return (
                <option value={item.value} key={key}>
                  {item.label}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    );
  }
}

export default withRouter(withStyles(stylePage)(withTranslation()(ExchangeInput)));
