export const APP_SETTINGS = JSON.parse(localStorage.getItem('APP_SETTINGS')) !== null ? JSON.parse(localStorage.getItem('APP_SETTINGS')) : [{}];

const stylePage = (theme) => ({
  root: {
    flexGrow: '1',
    fontSize: '16px',
  },
  standardButton: {
    background: APP_SETTINGS.APP_COLOR2,
    color: '#FFFFFF',
    fontSize: '1rem',
    fontWeight: '500',
    textDecoration: 'none',
    transition: '0.28s ease',
    padding: '5px 10px',
    borderRadius: '5px',
    margin: '0 0 0 -0.75rem',
    '&:hover': {
      background: APP_SETTINGS.APP_COLOR4,
    },
  },
  bannerImage1: {
    height: '41px',
    width: '208px',
    justifyContent: 'left',
    background: 'transparent',
    [theme.breakpoints.down('md')]: {
      height: '27px',
      width: '170px',
    },
  },
  howItWorksBlock01: {
    display: 'flex',
    margin: '10px 0px ',
    [theme.breakpoints.down('md')]: {
      margin: '10px 0px',
    },
  },
  bigButton: {
    background: '#00998c',
    color: '#FFFFFF',
    fontSize: '1.2rem',
    fontWeight: '5000',
    textDecoration: 'none',
    transition: '0.28s ease',
    padding: '8px 16px',
    borderRadius: '5px',
    width: 'max-content',
    display: 'flex',
    flexFlow: 'row wrap',
    alignItems: 'center',
    '&:hover': {
      background: APP_SETTINGS.APP_COLOR,
    },
  },
  container: {
    width: '100%',
    marginRight: 'auto',
    marginLeft: 'auto',
    maxWidth: '90%',
    [theme.breakpoints.up(1700)]: {
      maxWidth: '80%',
    },
    [theme.breakpoints.up(2000)]: {
      maxWidth: '60%',
    },
  },
  //HEADER
  list: {
    width: 250,
  },
  AppBar: {
    padding: '10px 0',
    background: '#ED3237',
    border: 'none',
    boxShadow: 'none',
  },
  AppBarLogo: {
    textAlign: 'left',
    maxWidth: '150px',
    display: 'flex',
    [theme.breakpoints.down('md')]: {
      textAlign: 'left',
      maxWidth: '130px',
    },
  },
  linksButton: {
    cursor: 'pointer',
    color: '#FFFFFF',
    gap: '24px',
    borderRadius: '15px',
    backgroundColor: 'transparent',
    '&:hover': {
      backgroundColor: '#004960',
    },
  },
  AppBarDrawer: {
    position: 'absolute',
    top: '1.5vh',
    right: '0',
    '& svg': {
      fontSize: '2rem',
      color: APP_SETTINGS.APP_COLOR3,
    },
    '& button': {
      padding: 0,
    },
    [theme.breakpoints.up('lg')]: {
      top: '3.5vh',
      right: '1.5rem',
      display: 'none',
    },
  },
  AppBarMenu: {
    display: 'flex',
    flexFlow: 'row wrap',
    alignItems: 'center',
    justifyContent: 'flex-end',
    fontSize: '1rem',
    fontWeight: '500',
    gap: '16px',
    [theme.breakpoints.down('md')]: {
      display: 'none',
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  links: {
    color: '#212121',
    fontSize: '1rem',
    fontWeight: '400',
    textDecoration: 'none',
    transition: '0.28s ease',
    display: 'flex',
    justifyContent: 'center',
    gap: '1.75rem',
    marginLeft: '2rem',
    '&:hover': {
      color: APP_SETTINGS.APP_COLOR4,
    },
    [theme.breakpoints.down('md')]: {
      //display: 'none',
    },
  },

  linksFeaturedAlt: {
    background: 'APP_SETTINGS.APP_COLOR1',
    color: '#fff',
    fontSize: '1rem',
    fontWeight: '500',
    textDecoration: 'none',
    transition: '0.28s ease',
    padding: '5px 10px',
    borderRadius: '5px',
    '&:hover': {
      background: APP_SETTINGS.APP_COLOR2,
    },
    [theme.breakpoints.down('md')]: {
      display: 'none',
    },
  },
  linksArrow: {
    alignItems: 'top',
    border: 'px solid #FFF',
    background: '#fff',
    color: '#555555',
    [theme.breakpoints.down('md')]: {
      marginTop: '0px',
      marginRight: '10px',
      alignItems: 'center',
      padding: '0px 1px',
    },
  },
  menuProfile: {
    fontSize: '0.5rem',
  },
});

export default stylePage;
